import React from 'react';
import Button from '@mui/material/Button';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import { useTheme } from '@mui/material/styles';
import profileImage from '../../assets/images/professional-photo.jpg';

const AboutMe = () => {
  const theme = useTheme();

  return (
    <Box
    sx={{
      backgroundColor: theme.palette.background.default,
      color: theme.palette.text.primary,
      minHeight: '100vh',
      minWidth: '100vw',
      padding: theme.spacing(4),
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      fontFamily: 'Roboto, Arial, sans-serif', // Explicitly set the font
      boxSizing: 'border-box',
      overflow: 'hidden',
    }}
    >
      <Grid container spacing={4} justifyContent="center" alignItems="center">
        <Grid item xs={12} sm={6} md={4}>
          <img 
            src={profileImage} 
            alt="Profile" 
            style={{ 
              maxWidth: '100%', 
              height: 'auto', 
              borderRadius: '8px',
              boxSizing: 'border-box', // New addition
            }}
          />
        </Grid>
        <Grid item xs={12} sm={8} md={6} style={{ textAlign: 'center', padding: theme.spacing(2) }}> {/* Adjusted styling */}
          <h1>Hello! I'm Anthony Mercadante</h1>
          <p>Founder of Raethexn Technologies, specializing in custom software solutions.
             I'm also a 3rd year Software Development student at Mohawk College, 
             driven by a lifelong passion for technology.
          </p>
          <Button 
            variant="contained" 
            color="primary" 
            sx={{ marginTop: theme.spacing(2) }}
          >
            View My Skills
          </Button>
        </Grid>
      </Grid>
    </Box>
  );
};

export default AboutMe;
