import React from 'react';
import Button from '@mui/material/Button';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import { useTheme } from '@mui/material/styles';

const AboutMe = () => {
  const theme = useTheme();

  return (
    <Box
      sx={{
        backgroundColor: theme.palette.background.default,
        color: theme.palette.text.primary,
        minHeight: '100vh',
        minWidth: '100vw',
        padding: theme.spacing(4),
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
      }}
    >
      <Grid container spacing={4}>
        <Grid item xs={12} md={6}>
          <img 
            src={'./../../assets/images/professional-photo.jpg'} 
            alt="Profile" 
            style={{ maxWidth: '100%', borderRadius: '8px' }} // Inline styling
          />
        </Grid>
        <Grid item xs={12} md={6} style={{ textAlign: 'left' }}> {/* Inline styling */}
          <h1>Hello! I'm Anthony Mercadante</h1>
          <p>Founder of Raethexn Technologies, specializing in custom software solutions.
             I'm also a 3rd year Software Development student at Mohawk College, 
             driven by a lifelong passion for technology.
          </p>
          <Button 
            variant="contained" 
            color="primary" 
            sx={{ marginTop: theme.spacing(2) }}
          >
            View My Skills
          </Button>
        </Grid>
      </Grid>
    </Box>
  );
};

export default AboutMe;
