import React from 'react';
import { Link } from 'react-router-dom';
import { Container, Typography, Button } from '@mui/material';

const HomePage = () => {
  return (
    <Container maxWidth={false} 
               style={{ 
                 position: 'relative',
                 backgroundColor: '#000', 
                 color: '#fff', 
                 minHeight: '100vh', 
                 display: 'flex', 
                 flexDirection: 'column', 
                 justifyContent: 'center', 
                 alignItems: 'center',
                 textAlign: 'center',
                 padding: '20px', 
               }}>
      <div className="floating-arrow" style={{
        position: 'absolute',
        top: '20%', // Adjust as necessary
        left: '50%',
        transform: 'translateX(-50%) rotate(-45deg)',
        animation: 'float 2s ease-in-out infinite',
        color: '#fff', // Text color
        // Add additional styling for the arrow appearance
      }}>
        <span>ðŸ‘†Click Me</span>
      </div>
      <Typography variant="h1" component="h1" gutterBottom>
        Anthony Mercadante
      </Typography>
      <Typography variant="body1" style={{ maxWidth: '600px', marginBottom: '20px' }}>
        I'm a full-stack software developer. Here you can find my portfolio, 
        a small glimpse of recently worked on projects using various programming languages.
      </Typography>
      <Button variant="contained" color="primary" component={Link} to="/portfolio">
        View My Work
      </Button>
    </Container>
  );
};

export default HomePage;
