import React from 'react';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import Paper from '@mui/material/Paper';
import Typography from '@mui/material/Typography';
import { styled } from '@mui/material/styles';
import Chip from '@mui/material/Chip';
import skillImage1 from '../../assets/images/python-analytics-visualization.png';
import skillImage2 from '../../assets/images/AIIntelligentSystems.png';

const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
  ...theme.typography.body2,
  padding: theme.spacing(2),
  display: 'flex', // Added flex display
  alignItems: 'center', // Align items vertically
  color: theme.palette.text.secondary,
}));

const SkillImage = styled('img')({
  width: '150px', // Set a fixed width
  height: '150px', // Set a fixed height
  borderRadius: '20px',
  objectFit: 'cover', // Maintain aspect ratio
  marginRight: theme.spacing(2), // Add some space between the image and text
});

const Skills = () => {
    const skills = [
      {
        title: 'Python Analytics & Visualization',
        skillsList: [
          'Python programming',
          'Statistical Analysis',
          'Data visualization',
          'Data cleaning techniques',
          'Linear regression',
          'Graph interpretation'
        ],
        description: 'Proficient in Python for analyzing and visualizing data, with a focus on practical applications in statistics, data cleaning, and trend analysis.',
        imageUrl: skillImage1,
      },
      {
        title: 'Artificial Intelligence & Intelligent Systems',
        skillsList: [
            'AI fundamentals',
            'System design for AI applications',
            'prototyping intelligent systems'
        ],
        description: 'Understanding of AI principles and ability to develop intelligent systems, encompassing both theoretical aspects and practical applications.',
        imageUrl: skillImage2,  
      },
      // ... add other skills here
    ];
  
    return (
        <Box
          sx={{
            p: 3,
            backgroundColor: 'black',
            color: 'white',
          }}
        >
          <Grid container spacing={3}>
            {skills.map((skill, index) => (
              <Grid item xs={12} sm={6} md={4} key={index}>
                <Item>
                  <SkillImage src={skill.imageUrl} alt={skill.title} />
                  <Box>
                    <Typography variant="h5" component="h3">{skill.title}</Typography>
                    <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1, marginTop: 2 }}>
                      {skill.skillsList.map((skillItem, skillIndex) => (
                        <Chip label={skillItem} key={skillIndex} color="primary" />
                      ))}
                    </Box>
                    <Typography variant="body2" sx={{ marginTop: 2 }}>{skill.description}</Typography>
                  </Box>
                </Item>
              </Grid>
            ))}
          </Grid>
        </Box>
      );
    };
    
    export default Skills;
